package day3;

public class Szeyun2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        /*
         * 写一个四则运算并进行输出
         * 变量，第一步，设定好值
         */
		
		int a=5;
		int b=2;
	   System.out.println("加"+(a+b));
	   System.out.println("减"+(a-b));
	   System.out.println("乘"+(a*b));
	   System.out.println("除"+(a/b));
	}

}
